const checkValue=e=>{let t=e.parentNode.querySelector("span");if(0==e.value)return t.textContent="",t.classList.remove("ps-1"),!1;switch(e.name){case"email":e.value.length<8?(t.classList.add("ps-1"),t.textContent="Characters must be more than 8"):/^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/.test(e.value)?(t.textContent="",t.classList.remove("ps-1")):(t.textContent="Email invalid",t.classList.add("ps-1"));break;case"username":case"password":e.value.length>8?(t.textContent="",t.classList.remove("ps-1")):(t.textContent="Characters must be more than 8",t.classList.add("ps-1"));break;default:let s=document.querySelector("input[name='password']");t.textContent=s.value==e.value?"":"Passwords do not match"}};